import './App.css';
import React from 'react';
import './index.css';

// function App() {
//   return (
//     <div className="App">
//     </div>
//   );
// }

// export default App;

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      value_loc: '',
      value_num: ''
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    const direc = event.target.getAttribute("mode");
    if (direc == "loc") {
      this.setState({value_loc: event.target.value});
    }
    if (direc == "num") {
      this.setState({value_num: event.target.value});
    }
  }

  handleSubmit(event) {
    if (isNaN(+this.state.value_num)) {
      alert('Please enter a valid number');
    }
    else {
      alert('A name was submitted: ' + this.state.value_loc + this.state.value_num);
      event.preventDefault();
    }
  }

  render() {
    return (
      <div>
      <header className="App-header">
      put stuff here
      </header>
      <form onSubmit={this.handleSubmit} className = "Main">
        <div className = "inputform">
        <label>
          Location:
          <input mode="loc" type="text" value={this.state.value_loc} onChange={this.handleChange} />
        </label>
        <label>
          Number of similar counties:
          <input mode="num" type="text" value={this.state.value_num} onChange={this.handleChange} />
        </label>
        <div className="submitbutton">
        <input type="submit" value="Submit" />
        </div>
        </div>
      </form>
      </div>
    );
  }
}
export default App;
