import './App.css';
import React from 'react';
import './index.css';

// function App() {
//   return (
//     <div className="App">
//     </div>
//   );
// }

// export default App;

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      value_loc: '',
      value_num: 3
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleClick = this.handleClick.bind(this);
  }

  handleChange(event) {
    const direc = event.target.getAttribute("mode");
    if (direc == "loc") {
      this.setState({value_loc: event.target.value});
    }
    if (direc == "num") {
      this.setState({value_num: event.target.value});
    }
  }

  handleSubmit(event) {
    if (isNaN(+this.state.value_num)) {
      alert('Please enter a valid number');
    }
    else if (this.state.value_num > 6 || this.state.value_num < 1) {
      alert('Please enter a number between 1 and 6');
    }
    else {
      alert('A name was submitted: ' + this.state.value_loc + this.state.value_num);
      event.preventDefault();
    }
  }

  handleClick(event) {
    event.preventDefault();
    const val = event.target.getAttribute("mode");
    if (val === "plus") {
      if (this.state.value_num+1 > 6) {
        alert('Please enter a number between 1 and 6');
      }
      else {
         this.setState({value_num: this.state.value_num+1});
      }
    }
    if (val === "minus") {
      if (this.state.value_num-1 < 1) {
        alert('Please enter a number between 1 and 6');
      }
      else {
        this.setState({value_num: this.state.value_num-1});
      }
    }
  }

  render() {
    return (
      <div className = "page">
      <header className="App-header">
        <div className="logo"> CounText
          </div>
          <div className="headercontent"> A comparison tool for journalists who need to make sense of their data - through context.
          </div>
      </header>
      <form onSubmit={this.handleSubmit} className = "Main">
        <div className = "inputform">
          <label>
            Enter a location for comparison, and the number of counties to compare it to:
            </label>
        <label>
          Location:
          <input mode="loc" type="text" value={this.state.value_loc} onChange={this.handleChange} />
        </label>
        <div className="similarcounties">
        <label>
          Number of similar counties:
          <input id="simcount" mode="num" type="text" value={this.state.value_num} onChange={this.handleChange} />
        </label>
        <div className="updownbutton">
          <button className="plusminbutton" onClick={this.handleClick} mode="plus">+</button>
          <button className="plusminbutton" onClick={this.handleClick} mode="minus">-</button>
          </div>
          </div>
        <div className="submitbutton">
        <input type="submit" value="Search" />
        </div>
        </div>
      </form>
      <div className="descrip">
        <h1>
        How does it work?
        </h1>
        <h2>
        We talked to data journalists around the world to determine what they think makes two locations “statistically similar.” The result is a mix of different data points like population, demographics, income rates, etc. To use it, just type in the county (or other related location) you’re researching, how many similar counties you’re looking for, and CounText will do the rest!
        </h2>
        <h1>
        Why is it important?
        </h1>
        <h2>
        Numbers do not always equal truth. When reporting with data, simply letting the numbers speak for themselves can have detrimental effects on your story. Take a simple example: If you were looking at crime rates in Chicago, trying to compare those to crime rates in a tiny town in Oklahoma would not be easy to understand. By comparing a location’s data to corresponding data from similar locales, it allows the journalist to identify patterns, outliers, and the general universe of the data. That’s why we made CounText - we take a complicated step out of your research process to get you to your story faster and better.        </h2>
      </div>
      </div>
    );
  }
}
export default App;
